import React, { useState } from 'react';
import {} from 'react-router-dom'
const UserInput = () => {
    const [user,setUser]=useState({
        name:"",email:"",phone:"",company:"",country:""
    });
    let name,value
    const handleChange=(e)=>{
       
        name=e.target.name;
        value=e.target.value;

        setUser({...user,[name]:value})
    }
    const haandleClick=async(e)=>{
        e.preventDefault();
       const res=await fetch('/contact',{
         method:'POST',
         header:{
            "Content-Type":""
         }
       })
    }

  return (
    <>
    <div>
        <form>
        <div>
            <label htmlFor='name'><i className="zmdi zmdi-account material-icons-name"></i></label>
            <input type='text' autoComplete='off' name='name' value={user.name} onChange={handleChange} placeholder='name'/>
        </div>
        <div>
            <label htmlFor='name'><i className="zmdi zmdi-email material-icons-name"></i></label>
            <input type='text' autoComplete='off' name='email' value={user.email} onChange={handleChange} placeholder='name'/>
        </div>
        <div>
            <label htmlFor='name'><i className="zmdi zmdi-phone material-icons-name"></i></label>
            <input type='text' autoComplete='off' name='phone' value={user.phone} onChange={handleChange} placeholder='name'/>
        </div>
        <div>
            <label htmlFor='name'><i className="zmdi zmdi-desktop-windows material-icons-name"></i></label>
            <input type='text' autoComplete='off' name='company' value={user.company} onChange={handleChange} placeholder='name'/>
        </div>
        <div>
            <label htmlFor='name'><i className="zmdi zmdi-pin material-icons-name"></i></label>
            <input type='text' autoComplete='off' name='country' value={user.country} onChange={handleChange} placeholder='name'/>
        </div>
        <div>
            <input type='submit'  name="signup" value='Sign-Up' onClick={haandleClick} className='btn'/>
        </div>
        </form>
    </div>
    </>
  );
}

export default UserInput;
